const express = require('express')
const cors = require( 'cors');
const bodyParser = require('body-parser')
const uuid = require('uuid')
const cookieParser = require('cookie-parser')
require('dotenv').config()
const mongoose = require('mongoose');
const router = require('./router');
const errorMiddleware = require('./middlewares/error-middleware');



const jsonParser = bodyParser.json()


const app = express();
const port = process.env.PORT || 5000

const img = 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR-OW7aRmdoMOIBaQZOMX8mRucpWCt1DIsymIqVxjIllA&s'

const friends = {
    friends: [
        {id: uuid.v4(), first_name: "Alex", last_name: 'Berton', country : 'USA', city: 'New York', status: 'Live is strange!', img: 'data:image/png;base64,', subscribe: false, },
        {id: uuid.v4(), first_name: "Bill", last_name: 'Mur', country : 'Brasil', city: 'Rio', status: 'Live is cooke!', img: 'data:image/png;base64,', subscribe: false },
        {id: uuid.v4(), first_name: "Peater", last_name: 'San', country : 'USA', city: 'Boston', status: 'Live is strange!', img: 'data:image/png;base64,', subscribe: true },
        {id: uuid.v4(), first_name: "Sara", last_name: 'Pirson', country : 'Canada', city: 'Toronto', status: 'Greate spirit', img: 'data:image/jpeg;base64,', subscribe: true },
        {id: uuid.v4(), first_name: "Nick", last_name: 'Fox', country : 'Argentina', city: 'Bogota', status: 'Welcome back!', img, subscribe: true }
    ]
}
const auth = {
  login: [

  ]
}
const postForm = {
  message: [

  ]
}

app.use(cookieParser())
app.use(cors({
  credentials: true,
  origin: process.env.CLIENT_URL}));
app.use(function(req, res, next) {
  res.header("Access-Control-Allow-Origin", process.env.CLIENT_URL); // update to match the domain you will make the request from
  res.header("Access-Control-Allow-Headers", "Origin, X-Requested-With, Content-Type, Accept");
  next();
});
app.use(express.json())
app.use('/api', router)
app.use(errorMiddleware)


app.get('/cookie', (req,res) => {
  
  res.cookie('token', 'userData.refreshToken', {maxAge: 30 * 24 * 60 * 60 *1000} )
  res.send('<h1>SS</h1>')
})
app.get('/', function(req, res) {
  res.send('hello world');
});
app.get('/friends', function(req, res) {
    res.json(friends)
});
app.get('/auth', function(req, res) {
  res.json(auth)
});
app.get('/form', function(req, res) {
  res.json(postForm)
});
app.post('/auth', jsonParser, function(req, res){

  if(auth.login.length === 10)
    auth.login = []
  auth.login.push({...req.body})

})
app.post('/form', jsonParser, function(req, res){

  console.log(req.body)

  if(postForm.message.length === 10)
    postForm.message = []
  postForm.message.push({...req.body})
})

const start = async () =>{
  try{
    await mongoose.connect(process.env.URL_DB, {
      useNewUrlParser: true,
      useUnifiedTopology: true
    })
    app.listen(port, () => {
      console.log(`listening on port ${port}`)
    })
  } catch(e){
    console.log(e)
  }
}

start()